package com.example.microsleeps_detector

import android.Manifest
import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.content.ServiceConnection
import android.content.pm.PackageManager
import android.os.Build
import android.os.Bundle
import android.os.IBinder
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.core.content.ContextCompat
import androidx.navigation.fragment.findNavController
import com.example.microsleeps_detector.databinding.DashboardBinding

/**
 * A simple [Fragment] subclass as the default destination in the navigation.
 */
class Dashboard : Fragment() {

    private var _binding: DashboardBinding? = null
    private val binding get() = _binding!!

    private var serviceBound = false
    private var service: DrowsinessDetectionService? = null
    private var stateListener: ((String) -> Unit)? = null

    private val REQ_NOTIF = 1001
    private val REQ_CAMERA = 1002

    private val serviceConnection = object : ServiceConnection {
        override fun onServiceConnected(name: ComponentName?, binder: IBinder?) {
            val local = binder as? DrowsinessDetectionService.LocalBinder
            service = local?.getService()
            serviceBound = service != null
            // Subscribe to status updates
            stateListener = { state -> updateServiceStatus(state) }
            stateListener?.let { service?.addStateListener(it) }
        }

        override fun onServiceDisconnected(name: ComponentName?) {
            // Unsubscribe listeners
            stateListener?.let { service?.removeStateListener(it) }
            stateListener = null
            service = null
            serviceBound = false
        }
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = DashboardBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        binding.buttonFirst.setOnClickListener {
            findNavController().navigate(R.id.action_dashboard_to_camera)
        }

        binding.buttonStream.setOnClickListener {
            findNavController().navigate(R.id.action_dashboard_to_stream)
        }

        binding.startServiceBtn.setOnClickListener { ensurePermissionsAndStart() }
        binding.stopServiceBtn.setOnClickListener { stopBackgroundService() }

        // Toggle visibility of URL input depending on selected source
        binding.sourceRadioGroup.setOnCheckedChangeListener { _, checkedId ->
            val streamSelected = checkedId == R.id.radioSourceStream
            binding.streamUrlInput.visibility = if (streamSelected) View.VISIBLE else View.GONE
        }
        // Initialize visibility
        binding.streamUrlInput.visibility = if (binding.sourceRadioGroup.checkedRadioButtonId == R.id.radioSourceStream) View.VISIBLE else View.GONE

        // Try binding if service is already running
        bindToService()
    }

    private fun isPhoneSourceSelected(): Boolean =
        binding.sourceRadioGroup.checkedRadioButtonId == R.id.radioSourcePhone

    private fun ensurePermissionsAndStart() {
        // If phone camera selected, ensure CAMERA permission first
        if (isPhoneSourceSelected()) {
            val camGranted = ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.CAMERA) == PackageManager.PERMISSION_GRANTED
            if (!camGranted) {
                requestPermissions(arrayOf(Manifest.permission.CAMERA), REQ_CAMERA)
                return
            }
        }
        // Ensure notification permission on Android 13+
        if (Build.VERSION.SDK_INT >= 33) {
            val notifGranted = ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.POST_NOTIFICATIONS) == PackageManager.PERMISSION_GRANTED
            if (!notifGranted) {
                requestPermissions(arrayOf(Manifest.permission.POST_NOTIFICATIONS), REQ_NOTIF)
                return
            }
        }
        startBackgroundService()
    }

    private fun startBackgroundService() {
        val phoneSelected = isPhoneSourceSelected()
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java).apply {
            action = DrowsinessDetectionService.ACTION_START
            putExtra(DrowsinessDetectionService.EXTRA_SOURCE, if (phoneSelected) DrowsinessDetectionService.SOURCE_PHONE_CAMERA else DrowsinessDetectionService.SOURCE_STREAM)
            if (!phoneSelected) {
                val url = binding.streamUrlInput.text?.toString()?.trim().orEmpty()
                if (url.isEmpty()) {
                    Toast.makeText(requireContext(), "Ingresa la URL del stream", Toast.LENGTH_SHORT).show()
                    return
                }
                putExtra(DrowsinessDetectionService.EXTRA_STREAM_URL, url)
            }
        }
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            requireContext().startForegroundService(intent)
        } else {
            requireContext().startService(intent)
        }
        bindToService()
        updateServiceStatus("Iniciando…")
    }

    private fun stopBackgroundService() {
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java).apply {
            action = DrowsinessDetectionService.ACTION_STOP
        }
        requireContext().startService(intent)
        unbindFromService()
        updateServiceStatus("Estado del servicio: Inactivo")
    }

    private fun bindToService() {
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java)
        requireContext().bindService(intent, serviceConnection, Context.BIND_AUTO_CREATE)
    }

    private fun unbindFromService() {
        if (serviceBound) {
            stateListener?.let { service?.removeStateListener(it) }
            stateListener = null
            requireContext().unbindService(serviceConnection)
            serviceBound = false
        }
    }

    private fun updateServiceStatus(state: String) {
        binding.serviceStatusText.text = "Estado del servicio: $state"
    }

    override fun onRequestPermissionsResult(requestCode: Int, permissions: Array<out String>, grantResults: IntArray) {
        super.onRequestPermissionsResult(requestCode, permissions, grantResults)
        when (requestCode) {
            REQ_NOTIF -> {
                if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                    startBackgroundService()
                } else {
                    Toast.makeText(requireContext(), "Permiso de notificaciones denegado", Toast.LENGTH_SHORT).show()
                }
            }
            REQ_CAMERA -> {
                if (grantResults.isNotEmpty() && grantResults[0] == PackageManager.PERMISSION_GRANTED) {
                    // After camera granted, continue with notification check/start
                    ensurePermissionsAndStart()
                } else {
                    Toast.makeText(requireContext(), "Permiso de cámara denegado", Toast.LENGTH_SHORT).show()
                }
            }
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        unbindFromService()
        _binding = null
    }
}

package com.example.microsleeps_detector

import android.Manifest
import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.content.ServiceConnection
import android.content.pm.PackageManager
import android.os.Build
import android.os.Bundle
import android.os.IBinder
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.activity.result.contract.ActivityResultContracts.RequestPermission
import androidx.core.content.ContextCompat
import androidx.navigation.fragment.findNavController
import com.example.microsleeps_detector.databinding.DashboardBinding

/**
 * A simple [Fragment] subclass as the default destination in the navigation.
 */
class Dashboard : Fragment() {

    private var _binding: DashboardBinding? = null
    private val binding get() = _binding!!

    private var serviceBound = false
    private var service: DrowsinessDetectionService? = null
    private var stateListener: ((String) -> Unit)? = null

    private val REQ_NOTIF = 1001
    private val REQ_CAMERA = 1002

    // Activity Result launchers for modern permission handling
    private val cameraPermissionLauncher = registerForActivityResult(RequestPermission()) { granted ->
        if (granted) {
            // After camera granted, proceed to notification check/start
            checkNotificationAndStart()
        } else {
            Toast.makeText(requireContext(), "Permiso de cámara denegado", Toast.LENGTH_SHORT).show()
        }
    }

    private val notificationPermissionLauncher = registerForActivityResult(RequestPermission()) { granted ->
        if (granted) {
            startBackgroundService()
        } else {
            Toast.makeText(requireContext(), "Permiso de notificaciones denegado", Toast.LENGTH_SHORT).show()
        }
    }

    private val serviceConnection = object : ServiceConnection {
        override fun onServiceConnected(name: ComponentName?, binder: IBinder?) {
            val local = binder as? DrowsinessDetectionService.LocalBinder
            service = local?.getService()
            serviceBound = service != null
            // Subscribe to status updates
            stateListener = { state -> updateServiceStatus(state) }
            stateListener?.let { service?.addStateListener(it) }
        }

        override fun onServiceDisconnected(name: ComponentName?) {
            // Unsubscribe listeners
            stateListener?.let { service?.removeStateListener(it) }
            stateListener = null
            service = null
            serviceBound = false
        }
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = DashboardBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        binding.buttonFirst.setOnClickListener {
            findNavController().navigate(R.id.action_dashboard_to_camera)
        }

        binding.buttonStream.setOnClickListener {
            findNavController().navigate(R.id.action_dashboard_to_stream)
        }

        binding.startServiceBtn.setOnClickListener { ensurePermissionsAndStart() }
        binding.stopServiceBtn.setOnClickListener { stopBackgroundService() }

        // Toggle visibility of URL input depending on selected source
        binding.sourceRadioGroup.setOnCheckedChangeListener { _, checkedId ->
            val streamSelected = checkedId == R.id.radioSourceStream
            binding.streamUrlInput.visibility = if (streamSelected) View.VISIBLE else View.GONE
        }
        // Initialize visibility
        binding.streamUrlInput.visibility = if (binding.sourceRadioGroup.checkedRadioButtonId == R.id.radioSourceStream) View.VISIBLE else View.GONE

        // Try binding if service is already running
        bindToService()
    }

    private fun isPhoneSourceSelected(): Boolean =
        binding.sourceRadioGroup.checkedRadioButtonId == R.id.radioSourcePhone

    private fun ensurePermissionsAndStart() {
        // If phone camera selected, ensure CAMERA permission first via the launcher
        if (isPhoneSourceSelected()) {
            val camGranted = ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.CAMERA) == PackageManager.PERMISSION_GRANTED
            if (!camGranted) {
                cameraPermissionLauncher.launch(Manifest.permission.CAMERA)
                return
            }
        }
        // If not phone or camera already granted, check notifications permission next
        checkNotificationAndStart()
    }

    private fun checkNotificationAndStart() {
        if (Build.VERSION.SDK_INT >= 33) {
            val notifGranted = ContextCompat.checkSelfPermission(requireContext(), Manifest.permission.POST_NOTIFICATIONS) == PackageManager.PERMISSION_GRANTED
            if (!notifGranted) {
                notificationPermissionLauncher.launch(Manifest.permission.POST_NOTIFICATIONS)
                return
            }
        }
        startBackgroundService()
    }

    private fun startBackgroundService() {
        val phoneSelected = isPhoneSourceSelected()
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java).apply {
            action = DrowsinessDetectionService.ACTION_START
            putExtra(DrowsinessDetectionService.EXTRA_SOURCE, if (phoneSelected) DrowsinessDetectionService.SOURCE_PHONE_CAMERA else DrowsinessDetectionService.SOURCE_STREAM)
            if (!phoneSelected) {
                val url = binding.streamUrlInput.text?.toString()?.trim().orEmpty()
                if (url.isEmpty()) {
                    Toast.makeText(requireContext(), "Ingresa la URL del stream", Toast.LENGTH_SHORT).show()
                    return
                }
                putExtra(DrowsinessDetectionService.EXTRA_STREAM_URL, url)
            }
        }
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
            requireContext().startForegroundService(intent)
        } else {
            requireContext().startService(intent)
        }
        bindToService()
        updateServiceStatus(getString(R.string.service_status_starting))
    }

    private fun stopBackgroundService() {
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java).apply {
            action = DrowsinessDetectionService.ACTION_STOP
        }
        requireContext().startService(intent)
        unbindFromService()
        updateServiceStatus(getString(R.string.service_status_inactive))
    }

    private fun bindToService() {
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java)
        requireContext().bindService(intent, serviceConnection, Context.BIND_AUTO_CREATE)
    }

    private fun unbindFromService() {
        if (serviceBound) {
            stateListener?.let { service?.removeStateListener(it) }
            stateListener = null
            requireContext().unbindService(serviceConnection)
            serviceBound = false
        }
    }

    private fun updateServiceStatus(state: String) {
        // Use string resource with placeholder to avoid lint warning
        binding.serviceStatusText.text = getString(R.string.service_status, state)
    }

    override fun onDestroyView() {
        super.onDestroyView()
        unbindFromService()
        _binding = null
    }
}

package com.example.microsleeps_detector

import android.content.ComponentName
import android.content.Context
import android.content.Intent
import android.content.ServiceConnection
import android.graphics.Bitmap
import android.os.Bundle
import android.os.IBinder
import android.view.*
import com.example.microsleeps_detector.databinding.FragmentStreamBinding
import com.example.microsleeps_detector.ui.OverlayView

class StreamFragment : BaseFaceDetectionFragment<FragmentStreamBinding>() {

    override val binding get() = _binding!!

    // Usaremos la misma instancia del servicio que el Dashboard
    private var serviceBound = false
    private var service: DrowsinessDetectionService? = null

    // Listeners para suscribir/desuscribir limpiamente
    private var stateListener: ((String) -> Unit)? = null
    private var frameListener: ((Bitmap) -> Unit)? = null
    private var resultListener: ((FaceLandmarkerHelper.ResultBundle) -> Unit)? = null
    private var analysisListener: ((FaceAnalysis.Result) -> Unit)? = null
    private var emptyListener: (() -> Unit)? = null
    private var errorListener: ((String, Int) -> Unit)? = null

    // Evitar usar el listener del Activity y evitar alarma local (el servicio ya maneja alarma)
    override val useActivityLandmarkerListener: Boolean = false
    override val playLocalAlarm: Boolean = false

    private val serviceConnection = object : ServiceConnection {
        override fun onServiceConnected(name: ComponentName?, binder: IBinder?) {
            val local = binder as? DrowsinessDetectionService.LocalBinder
            service = local?.getService()
            serviceBound = service != null
            if (!serviceBound) return

            // Estado del servicio -> etiqueta inferior y estado visual
            stateListener = { state ->
                renderer?.setStatus(state)
                if (state == DrowsinessDetectionService.STATE_NOT_CONNECTED || state.startsWith("Se desconectó")) {
                    if (isAdded) {
                        requireActivity().runOnUiThread {
                            binding.streamImageView.setImageDrawable(null)
                            getOverlayView()?.clear()
                        }
                    }
                }
            }
            stateListener?.let { service?.addStateListener(it) }

            // Frame de vista previa -> mostrar en ImageView
            frameListener = { bmp ->
                // Post to ImageView to ensure main thread without needing early return labels
                binding.streamImageView.post {
                    if (isAdded) {
                        binding.streamImageView.setImageBitmap(bmp)
                    }
                }
            }
            frameListener?.let { service?.addFrameListener(it) }

            // Resultados y análisis -> overlay y labels
            resultListener = { bundle ->
                if (isAdded) {
                    requireActivity().runOnUiThread {
                        getOverlayView()?.setResults(bundle)
                    }
                }
            }
            resultListener?.let { service?.addResultListener(it) }

            analysisListener = { result ->
                renderer?.render(result)
            }
            analysisListener?.let { service?.addAnalysisListener(it) }

            emptyListener = {
                if (isAdded) {
                    renderer?.setStatus("No face detected")
                    requireActivity().runOnUiThread { getOverlayView()?.clear() }
                }
            }
            emptyListener?.let { service?.addEmptyListener(it) }

            errorListener = { error, _ ->
                renderer?.setStatus("Error: $error")
            }
            errorListener?.let { service?.addErrorListener(it) }
        }

        override fun onServiceDisconnected(name: ComponentName?) {
            unsubscribeAll()
            service = null
            serviceBound = false
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setHasOptionsMenu(true)
    }

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentStreamBinding.inflate(inflater, container, false)
        return binding.root
    }

    override fun onViewCreatedImpl(view: View, savedInstanceState: Bundle?) {
        // Mostrar estado inicial
        renderer?.setStatus(DrowsinessDetectionService.STATE_NOT_CONNECTED)
        // Vincular al servicio (si ya está corriendo lo reutiliza; si no, solo mostrará estado)
        bindToService()
    }

    override fun onCreateOptionsMenu(menu: Menu, inflater: MenuInflater) {
        inflater.inflate(R.menu.menu_stream, menu)
        super.onCreateOptionsMenu(menu, inflater)
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        return when (item.itemId) {
            R.id.action_stop_service -> {
                val intent = Intent(requireContext(), DrowsinessDetectionService::class.java).apply {
                    action = DrowsinessDetectionService.ACTION_STOP
                }
                requireContext().startService(intent)
                true
            }
            else -> super.onOptionsItemSelected(item)
        }
    }

    override fun getOverlayView(): OverlayView? = binding.overlay

    override fun onPauseImpl() {
        // No detener el servicio aquí: compartido entre fragments
    }

    override fun onDestroyViewImpl() {
        unbindFromService()
    }

    private fun bindToService() {
        val intent = Intent(requireContext(), DrowsinessDetectionService::class.java)
        requireContext().bindService(intent, serviceConnection, Context.BIND_AUTO_CREATE)
    }

    private fun unbindFromService() {
        if (serviceBound) {
            unsubscribeAll()
            requireContext().unbindService(serviceConnection)
            serviceBound = false
        }
    }

    private fun unsubscribeAll() {
        stateListener?.let { service?.removeStateListener(it) }
        frameListener?.let { service?.removeFrameListener(it) }
        resultListener?.let { service?.removeResultListener(it) }
        analysisListener?.let { service?.removeAnalysisListener(it) }
        emptyListener?.let { service?.removeEmptyListener(it) }
        errorListener?.let { service?.removeErrorListener(it) }
        stateListener = null
        frameListener = null
        resultListener = null
        analysisListener = null
        emptyListener = null
        errorListener = null
    }
}

